import React, { useEffect } from "react";
import { Button,Table,Container } from "react-bootstrap";


function Prolist(props) {







    return ( 
        <div> 
    
       
        <Container className='cartnum'>
             <Table className='mt-4'>
   
      
      
     <tbody   >
       <tr>
         <td  className='m-0 p-0 td'><img src={props.image} className='cartim'/><br/><h5 className='mt-3 '>{props.name}</h5></td>
         <td className='text-center cartval  '><Button onClick={props.incrementItem} className='btn-secondary bttn'>+</Button ><span className='m-2' >{props.quantity}</span><Button onClick={props.decrementItem} className='btn-danger bttn'>-</Button></td>
         <td className='text-center cartval'>${props.price}</td>
         <td className='text-center cartval'>${props.price*props.quantity}</td>
         <td className='text-center cartval'><Button className='btn-danger' onClick={props.removeItem}>Remove</Button></td>
       </tr>
     </tbody>
    

    </Table>

    </Container>
    
   </div>
     );
}

export default Prolist ;
