import React from 'react';
import { useDispatch, useSelector} from 'react-redux';
import Prolist from './productlistitem';
import { incrementQuantity } from './reducers/cartreducer';
import { decrementQuantity } from './reducers/cartreducer';
import { remove } from './reducers/cartreducer';




function Cart() {
    const list = useSelector((state) => state.cart.product)
const dispatch = useDispatch();

const removeItem = (item) =>{
  dispatch(remove(item))
}
const incrementItem = (item) =>{
  dispatch(incrementQuantity(item))
}

const decrementItem= (item) =>{
  dispatch(decrementQuantity(item))
}
subtotal = list.price*list.quantity

const total = list.reduce((total, subtotal) => total + subtotal)




    return (
      <>
   {list.map((item) => <Prolist {...item} key={item.id} incrementItem={()=> incrementItem(item)} decrementItem={()=> decrementItem(item)} removeItem={()=> removeItem(item)}      />)}
  <span>{total}</span>
   </>

      );
}

export default Cart;
