import React from 'react';

import { useDispatch, useSelector} from 'react-redux';
import Prolist from './productlistitem';
import { modify } from './reducers/cartreducer';


const dispatch = useDispatch();
const incrementItem = (item) =>{
  dispatch(modify({...item,count : item.count + 1}))
}


function Cart() {
    const list = useSelector((state) => state.cart.product)

    return (
      <>
   {list.map((item) => <Prolist {...item} key={item.id} incrementItem={()=> incrementItem(item)} />)}
   </>
      );
}

export default Cart;
