import React, { useState } from 'react';
import { Button, Container,Table} from 'react-bootstrap';
import { useSelector } from 'react-redux';

function Cart() {
    const list = useSelector((state) => state.cart.product)
  
    return (
    <div> 
    
       
        <Container className=' cartnum'><h2>CART</h2></Container>
        
              <Table >
      <thead>
        <tr className='text-center'>
          <th>Product</th>
          <th>Quantity</th>
          <th>Price</th>
          <th>Total</th>
          <th></th>
        </tr>
      </thead>
        <Container className='cartnum'>
       {list.map( item =>
       
      <tbody  key={item.id} >
        <tr>
          <td className='m-0 p-0 td'><img src={item.image} className='cartim'/><br/><h5 className='mt-3 ms-3'>{item.name}</h5></td>
          <td className='text-center cartval  '><Button className='btn-secondary bttn'>+</Button><span className='m-2' >1</span><Button className='btn-danger bttn'>-</Button></td>
          <td className='text-center cartval'>${item.price}</td>
          <td className='text-center cartval'>$100</td>
          <td className='text-center cartval'><Button className='btn-danger'>Remove</Button></td>
        </tr>
      </tbody>
        )
}</Container>
     </Table>
    </div>
      );
}

export default Cart;
