import {createSlice} from '@reduxjs/toolkit';

export const cartSlice = createSlice( {
    
    name:'cart',
    initialState : {
   
    product:[],
  
 },
reducers:{
    addtocart:(state ,{payload})=>{
      const itemInCart = state.product.find((item) => item.id === payload.id);
      if (itemInCart) {
        itemInCart.quantity++;
      } else {
        state.product.push({ ...payload, quantity: 1,subtotal:product.price });
      }
    },
    remove:(state, {payload})=> {
      const removeItem = state.product.filter((item) => item.id !== payload.id);
      state.product = removeItem;
      },
      incrementQuantity: (state,{payload} ) => {
        const item = state.product.find((item) => item.id === payload.id);
        item.quantity++;
      },
      decrementQuantity: (state, {payload}) => {
        const item = state.product.find((item) => item.id === payload.id);
        if (item.quantity === 1) {
          item.quantity = 1
        } else {
          item.quantity--;
        }
      },
      Total: (state,{payload} ) => {
        const item = state.product.find((item) => item.id === payload.id);
        item.subtotal
      },
},

});

export const {addtocart, remove, incrementQuantity,decrementQuantity} = cartSlice.actions;

export default cartSlice.reducer
