import React from 'react';
import { Container,Row,Col } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import {useParams} from "react-router-dom"




function Productdes() {

    const Pros = useSelector(state => state.Pros)
    const {id} = useParams();
   const pid = id; 

   
const product = Pros.find(product => product.id === parseInt (pid));

const image = "../" + product.image;
   

    return ( 
        <>
        <Container className='pdcn mt-4'>
            <Row>
              <Col lg="4" md='6' sm='6' >
       <div  className="pdbi" style={{backgroundImage:`url(${image})`}} ></div>
         <div>
        <h5> {product.name}</h5>
        <h3>${product.price}</h3>
        </div> 
              </Col>
              <Col lg={{span:7,offset:1}} md={{span:5,offset:1}} >
              <div>
             
              </div>
              </Col> 
          


                
                <Col>

                </Col>
            </Row>
        </Container>
        </>
     );
}

export default Productdes;
