import { CarouselItem } from "react-bootstrap";

const initialstate = {
    name:'cart',
    product:[],
    price:0,
    totalamount:0
 }


 const cartReducer = (state = initialstate,payload) =>{
    
        
          
           state.product = {...state.product,payload}

         
    
 }

 export default cartReducer;
