import { CarouselItem } from "react-bootstrap";

const initialstate = {
    name:'cart',
    product:[],
    price:0,
    totalamount:0
 }


 const cartReducer = (state = initialstate,action,payload) =>{
    switch(action.type){
        case "ADD_CART":
            return state.product = {...state,payload}

        default:
            return state    
    }
 }

 export default cartReducer;
