import React, { useState } from 'react';
import { Container,Row,Col,Table } from 'react-bootstrap';
import { useSelector } from 'react-redux';

function Cart() {
    const list = useSelector((state) => state.cart.product)
  
    return (
    <div> 
    
       
        <Container className='text-center ms-2 '><h1>CARTPAGE</h1></Container>
         {list.map( item =>
         <Container>
            <Table className='cartnum' key={item.id}>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                    </tr>
                </thead>
                <Row>
       <Col lg="2"> <div><img src={item.image} className='cartimage' /></div></Col>
       <Col lg='8'> 
       <div className='p-0 mt-4'>
       <span>{item.name}</span>
       <span>{item.price}</span>
       </div>
      </Col>
        </Row>
        </Table>
        </Container>)
       
}
    </div>
      );
}

export default Cart;
