import React from 'react';

import { useDispatch, useSelector} from 'react-redux';
import Prolist from './productlistitem';
import { modify } from './reducers/cartreducer';
import { remove } from './reducers/cartreducer';




function Cart() {
    const list = useSelector((state) => state.cart.product)
const dispatch = useDispatch();
const incrementItem = (item) =>{
  dispatch(modify({...item,count : item.count + 1}))
}

const removeItem = (item) =>{
  dispatch(remove(item))
}

    return (
      <>
   {list.map((item) => <Prolist {...item} key={item.id} incrementItem={()=> incrementItem(item)} removeItem={()=> removeItem(item)} />)}
   </>
      );
}

export default Cart;
