import {createSlice} from '@reduxjs/toolkit';

export const cartSlice = createSlice( {
    
    name:'cart',
    initialState : {
   
    product:[ ],
  
 },
reducers:{
    addtocart:(state ,{payload})=>{
        state.product = [...state.product ,{...payload, count:1}];
    },
    remove:(state, {payload})=> {
        const index = state.product.findIndex( (product) => product.id === parseInt (payload.id)
         ) ; 
       state.product = [
         ...state.product.slice(0,index),
         ...state.list.slice(index + 1)
       ];
      },
      modify:(state, {payload})=> {
        const index = state.product.findIndex( (product) => product.id === parseInt (payload.id)
         ) ; 
       state.product = [
         ...state.product.slice(0,index),
         {...state.product[index], count: payload.count},
         ...state.list.slice(index + 1)
       ];
      },
},

});

export const {addtocart, remove, modify} = cartSlice.actions;

export default cartSlice.reducer
