import {createSlice} from '@reduxjs/toolkit';

export const cartSlice = createSlice( {
    
    name:'cart',
    initialState : {
   
    product:[],
  
 },
reducers:{
    addtocart:(state ,{payload})=>{
        state.product = [...state.product ,{...payload, quantity:1}];
    },
    remove:(state, {payload})=> {
      const removeItem = state.product.filter((item) => item.id !== payload.id);
      state.product = removeItem;
      },
      incrementQuantity: (state, action) => {
        const item = state.product.find((item) => item.id === action.payload);
        item.quantity++;
      },
      decrementQuantity: (state, action) => {
        const item = state.product.find((item) => item.id === action.payload);
        if (item.quantity === 1) {
          item.quantity = 1
        } else {
          item.quantity--;
        }
      },
},

});

export const {addtocart, remove, incrementQuantity,decrementQuantity} = cartSlice.actions;

export default cartSlice.reducer
