import React from 'react';
import { useDispatch, useSelector} from 'react-redux';

import { incrementQuantity } from './reducers/cartreducer';
import { decrementQuantity } from './reducers/cartreducer';
import { remove } from './reducers/cartreducer';
import { Container, Table,Button,Row,Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { gettotal } from './reducers/cartreducer';


function Cart() {
    const list = useSelector((state) => state.cart.product)
    const list2 = useSelector((state) => state.cart)
const dispatch = useDispatch();
const Navigate = useNavigate();

const removeItem = (item) =>{
  dispatch(remove(item))
}
const incrementItem = (item) =>{
  dispatch(incrementQuantity(item))
}

const decrementItem= (item) =>{
  dispatch(decrementQuantity(item))
}

useEffect(() => {
  dispatch(gettotal());
}, [list])



    return (
      <>
        {list.length === 0 ? (
       <Container className='cartnum text-center'>
            <h5 className='mt-5'>Your cart is empty</h5>
            <Button className='btn-danger mt-3' onClick={() =>Navigate('/')}>Start Shopping</Button>
          </Container>
        ):(
          <div>
           <Container className='scrtable'>
             <Table className='mt-4 '>
             <thead>
       <tr className='text-center'>
         <th>Product</th>
         <th>Quantity</th>
         <th>Price</th>
         <th>Total</th>
         <th></th>
       </tr>
     </thead>
     <tbody   >
      {list.map((item) => (  <tr key={item.id}>
         <td  className='m-0 p-0 td'><img src={item.image} className='cartim'/><br/><h5 className='mt-3 '>{item.name}</h5></td>
         <td className='text-center cartval  '><Button onClick={() => incrementItem(item)} className='btn-secondary bttn'>+</Button ><span className='m-2' >{item.quantity}</span><Button onClick={() => decrementItem(item)} className='btn-danger bttn'>-</Button></td>
         <td className='text-center cartval'>${item.price}</td>
         <td className='text-center cartval'>${item.price*item.quantity}</td>
         <td className='text-center cartval'><Button className='btn-danger' onClick={() => removeItem(item)}>Remove</Button></td>
       </tr>))}
     
     </tbody>
    

    </Table>
   
    </Container> 
    <Container className='cartnum'>
        <Row>
          <Col>
          <span>Enter Promo code</span>
          <input placeholder='Promo Code'></input>
          <Button className='btn-danger'>APPLY</Button>
          </Col>
          <Col>
          <span>total:${list2.total}</span>
          </Col>
        </Row>
       </Container>
    </div>
        )}
       
     


   </>

      );
}

export default Cart;
