import React, { useState } from 'react';
import { Button, Container,Table} from 'react-bootstrap';
import { useSelector,useDispatch } from 'react-redux';
import { remove } from './reducers/cartreducer';

function Cart() {
    const list = useSelector((state) => state.cart.product)
    const dispatch = useDispatch();

    const removeItemFromCart = () => {
      dispatch(remove(list.id));

    };
  
    return (
    <div> 
    
       
         <Container className='cartnum'>
              <Table className='mt-4'>
      <thead>
        <tr className='text-center'>
          <th>Product</th>
          <th>Quantity</th>
          <th>Price</th>
          <th>Total</th>
          <th></th>
        </tr>
      </thead>
       
       {list.map( item =>(
       
      <tbody  key={item.id} >
        <tr>
          <td className='m-0 p-0 td'><img src={item.image} className='cartim'/><br/><h5 className='mt-3 '>{item.name}</h5></td>
          <td className='text-center cartval  '><Button className='btn-secondary bttn'>+</Button><span className='m-2' >1</span><Button className='btn-danger bttn'>-</Button></td>
          <td className='text-center cartval'>${item.price}</td>
          <td className='text-center cartval'>$100</td>
          <td className='text-center cartval'><Button className='btn-danger' onclick={dispatch(removeItemFromCart)}>Remove</Button></td>
        </tr>
      </tbody>)
        )
}
     </Table>
     </Container>
    </div>
      );
}

export default Cart;
